 <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBMrCWSExqZwKXirdwQ26-IyodnHtjv4lA&v=3.exp"></script>
    <script>
  var citymap = {};
	<?php 
	$q=mysqli_query($connection,"select * from rawan_bencana where keterangan='Baru' ");
			while ($d=mysqli_fetch_array($q)){
	$lat = $d['lat'];
	$lg = $d['lng'];
	$kecamatan = $d['kecamatan'];
	$radius = $d['radius'];
	?>

  citymap['<?php echo $kecamatan ?>'] = {
    center: new google.maps.LatLng(<?php echo $lat; ?>,<?php echo $lg; ?>),
    population: <?php echo $radius ?>
  };
  <?php  }?>
 
  var cityCircle;
  
  function initialize() {
     var mapOptions = {
   zoom: 8.7,
   center: new google.maps.LatLng(-6.639126521481149, 106.06382230509632),
   mapTypeId: google.maps.MapTypeId.TERRAIN
    };
 
    var map = new google.maps.Map(document.getElementById('map-canvas'),
     mapOptions);
      
    for (var city in citymap) {
   var populationOptions = {
     strokeColor: '#FF0000',
     strokeOpacity: 0.8,
     strokeWeight: 2,
     fillColor: '#FF0000',
     fillOpacity: 0.35,
     map: map,
     center: citymap[city].center,
     radius: Math.sqrt(citymap[city].population) * 100
   };
   // Add the circle for this city to the map.
   cityCircle = new google.maps.Circle(populationOptions);
    }
 
  }
 
  google.maps.event.addDomListener(window, 'load', initialize);
    </script>
 
  

 

